import{_ as a,e as p}from"./app.2a7ffa2e.js";var s="/assets/f7696506a3c1c94621fcbad10341f1a8.aa7f2171.png",i="/assets/243b1466779a9e107ae3ef0155604a17.2f7d1922.png",t="/assets/fabd6dadd61a0aa342d7107213955a72.56a0552e.png",n="/assets/557d17e05ce90f18591c2305871af665.a4fb9e16.png",e="/assets/261011d237bec993821aa198b97ae8ce.540ca944.png",r="/assets/7c635d682bd3cdc421bb9eea33a5a413.a93d53a7.png",l="/assets/3104c8c3adf36e8931862fe8a0520f5d.178780a3.png";const o={},d=p('<h1 id="\u4ECE\u6570\u636E\u9875\u7684\u89D2\u5EA6\u770B-b-\u6811" tabindex="-1"><a class="header-anchor" href="#\u4ECE\u6570\u636E\u9875\u7684\u89D2\u5EA6\u770B-b-\u6811" aria-hidden="true">#</a> \u4ECE\u6570\u636E\u9875\u7684\u89D2\u5EA6\u770B B+ \u6811</h1><p>\u5927\u5BB6\u597D\uFF0C\u6211\u662F\u5C0F\u6797\u3002</p><p>\u5927\u5BB6\u80CC\u516B\u80A1\u6587\u7684\u65F6\u5019\uFF0C\u90FD\u77E5\u9053 MySQL \u91CC InnoDB \u5B58\u50A8\u5F15\u64CE\u662F\u91C7\u7528 B+ \u6811\u6765\u7EC4\u7EC7\u6570\u636E\u7684\u3002</p><p>\u8FD9\u70B9\u6CA1\u9519\uFF0C\u4F46\u662F\u5927\u5BB6\u77E5\u9053 B+ \u6811\u91CC\u7684\u8282\u70B9\u91CC\u5B58\u653E\u7684\u662F\u4EC0\u4E48\u5462\uFF1F\u67E5\u8BE2\u6570\u636E\u7684\u8FC7\u7A0B\u53C8\u662F\u600E\u6837\u7684\uFF1F</p><p>\u8FD9\u6B21\uFF0C\u6211\u4EEC<strong>\u4ECE\u6570\u636E\u9875\u7684\u89D2\u5EA6\u770B B+ \u6811</strong>\uFF0C\u770B\u770B\u6BCF\u4E2A\u8282\u70B9\u957F\u5565\u6837\u3002</p><p><img src="'+s+'" alt="\u56FE\u7247"></p><h2 id="innodb-\u662F\u5982\u4F55\u5B58\u50A8\u6570\u636E\u7684" tabindex="-1"><a class="header-anchor" href="#innodb-\u662F\u5982\u4F55\u5B58\u50A8\u6570\u636E\u7684" aria-hidden="true">#</a> InnoDB \u662F\u5982\u4F55\u5B58\u50A8\u6570\u636E\u7684\uFF1F</h2><p>MySQL \u652F\u6301\u591A\u79CD\u5B58\u50A8\u5F15\u64CE\uFF0C\u4E0D\u540C\u7684\u5B58\u50A8\u5F15\u64CE\uFF0C\u5B58\u50A8\u6570\u636E\u7684\u65B9\u5F0F\u4E5F\u662F\u4E0D\u540C\u7684\uFF0C\u6211\u4EEC\u6700\u5E38\u4F7F\u7528\u7684\u662F InnoDB \u5B58\u50A8\u5F15\u64CE\uFF0C\u6240\u4EE5\u5C31\u8DDF\u5927\u5BB6\u56FE\u89E3\u4E0BInnoDB \u662F\u5982\u4F55\u5B58\u50A8\u6570\u636E\u7684\u3002</p><p>\u8BB0\u5F55\u662F\u6309\u7167\u884C\u6765\u5B58\u50A8\u7684\uFF0C\u4F46\u662F\u6570\u636E\u5E93\u7684\u8BFB\u53D6\u5E76\u4E0D\u4EE5\u300C\u884C\u300D\u4E3A\u5355\u4F4D\uFF0C\u5426\u5219\u4E00\u6B21\u8BFB\u53D6\uFF08\u4E5F\u5C31\u662F\u4E00\u6B21 I/O \u64CD\u4F5C\uFF09\u53EA\u80FD\u5904\u7406\u4E00\u884C\u6570\u636E\uFF0C\u6548\u7387\u4F1A\u975E\u5E38\u4F4E\u3002</p><div class="custom-container tip"><p class="custom-container-title">TIP</p><p>\u56E0\u6B64\uFF0C<strong>InnoDB \u7684\u6570\u636E\u662F\u6309\u300C\u6570\u636E\u9875\u300D\u4E3A\u5355\u4F4D\u6765\u8BFB\u5199\u7684</strong>\uFF0C\u4E5F\u5C31\u662F\u8BF4\uFF0C\u5F53\u9700\u8981\u8BFB\u4E00\u6761\u8BB0\u5F55\u7684\u65F6\u5019\uFF0C\u5E76\u4E0D\u662F\u5C06\u8FD9\u4E2A\u8BB0\u5F55\u672C\u8EAB\u4ECE\u78C1\u76D8\u8BFB\u51FA\u6765\uFF0C\u800C\u662F\u4EE5\u9875\u4E3A\u5355\u4F4D\uFF0C\u5C06\u5176\u6574\u4F53\u8BFB\u5165\u5185\u5B58\u3002</p></div><p>\u6570\u636E\u5E93\u7684 I/O \u64CD\u4F5C\u7684\u6700\u5C0F\u5355\u4F4D\u662F\u9875\uFF0C<strong>InnoDB \u6570\u636E\u9875\u7684\u9ED8\u8BA4\u5927\u5C0F\u662F 16KB</strong>\uFF0C\u610F\u5473\u7740\u6570\u636E\u5E93\u6BCF\u6B21\u8BFB\u5199\u90FD\u662F\u4EE5 16KB \u4E3A\u5355\u4F4D\u7684\uFF0C\u4E00\u6B21\u6700\u5C11\u4ECE\u78C1\u76D8\u4E2D\u8BFB\u53D6 16K \u7684\u5185\u5BB9\u5230\u5185\u5B58\u4E2D\uFF0C\u4E00\u6B21\u6700\u5C11\u628A\u5185\u5B58\u4E2D\u7684 16K \u5185\u5BB9\u5237\u65B0\u5230\u78C1\u76D8\u4E2D\u3002</p><p>\u6570\u636E\u9875\u5305\u62EC\u4E03\u4E2A\u90E8\u5206\uFF0C\u7ED3\u6784\u5982\u4E0B\u56FE\uFF1A</p><p><img src="'+i+'" alt="\u56FE\u7247"></p><p>\u8FD9 7 \u4E2A\u90E8\u5206\u7684\u4F5C\u7528\u5982\u4E0B\u56FE\uFF1A</p><p><img src="'+t+'" alt="\u56FE\u7247"></p><p>\u5728 File Header \u4E2D\u6709\u4E24\u4E2A\u6307\u9488\uFF0C\u5206\u522B\u6307\u5411\u4E0A\u4E00\u4E2A\u6570\u636E\u9875\u548C\u4E0B\u4E00\u4E2A\u6570\u636E\u9875\uFF0C\u8FDE\u63A5\u8D77\u6765\u7684\u9875\u76F8\u5F53\u4E8E\u4E00\u4E2A\u53CC\u5411\u7684\u94FE\u8868\uFF0C\u5982\u4E0B\u56FE\u6240\u793A\uFF1A</p><p><img src="'+n+'" alt="\u56FE\u7247"></p><p>\u91C7\u7528\u94FE\u8868\u7684\u7ED3\u6784\u662F\u8BA9\u6570\u636E\u9875\u4E4B\u95F4\u4E0D\u9700\u8981\u662F\u7269\u7406\u4E0A\u7684\u8FDE\u7EED\u7684\uFF0C\u800C\u662F\u903B\u8F91\u4E0A\u7684\u8FDE\u7EED\u3002</p><div class="custom-container tip"><p class="custom-container-title">TIP</p><p>\u6570\u636E\u9875\u7684\u4E3B\u8981\u4F5C\u7528\u662F\u5B58\u50A8\u8BB0\u5F55\uFF0C\u4E5F\u5C31\u662F\u6570\u636E\u5E93\u7684\u6570\u636E\uFF0C\u6240\u4EE5\u91CD\u70B9\u8BF4\u4E00\u4E0B\u6570\u636E\u9875\u4E2D\u7684 User Records \u662F\u600E\u4E48\u7EC4\u7EC7\u6570\u636E\u7684\u3002</p></div><p><strong>\u6570\u636E\u9875\u4E2D\u7684\u8BB0\u5F55\u6309\u7167\u300C\u4E3B\u952E\u300D\u987A\u5E8F\u7EC4\u6210\u5355\u5411\u94FE\u8868</strong>\uFF0C\u5355\u5411\u94FE\u8868\u7684\u7279\u70B9\u5C31\u662F\u63D2\u5165\u3001\u5220\u9664\u975E\u5E38\u65B9\u4FBF\uFF0C\u4F46\u662F\u68C0\u7D22\u6548\u7387\u4E0D\u9AD8\uFF0C\u6700\u5DEE\u7684\u60C5\u51B5\u4E0B\u9700\u8981\u904D\u5386\u94FE\u8868\u4E0A\u7684\u6240\u6709\u8282\u70B9\u624D\u80FD\u5B8C\u6210\u68C0\u7D22\u3002</p><p>\u56E0\u6B64\uFF0C\u6570\u636E\u9875\u4E2D\u6709\u4E00\u4E2A<strong>\u9875\u76EE\u5F55</strong>\uFF0C\u8D77\u5230\u8BB0\u5F55\u7684\u7D22\u5F15\u4F5C\u7528\uFF0C\u5C31\u50CF\u6211\u4EEC\u4E66\u90A3\u6837\uFF0C\u9488\u5BF9\u4E66\u4E2D\u5185\u5BB9\u7684\u6BCF\u4E2A\u7AE0\u8282\u8BBE\u7ACB\u4E86\u4E00\u4E2A\u76EE\u5F55\uFF0C\u60F3\u770B\u67D0\u4E2A\u7AE0\u8282\u7684\u65F6\u5019\uFF0C\u53EF\u4EE5\u67E5\u770B\u76EE\u5F55\uFF0C\u5FEB\u901F\u627E\u5230\u5BF9\u5E94\u7684\u7AE0\u8282\u7684\u9875\u6570\uFF0C\u800C\u6570\u636E\u9875\u4E2D\u7684\u9875\u76EE\u5F55\u5C31\u662F\u4E3A\u4E86\u80FD\u5FEB\u901F\u627E\u5230\u8BB0\u5F55\u3002</p><hr><p>\u90A3 InnoDB \u662F\u5982\u4F55\u7ED9\u8BB0\u5F55\u521B\u5EFA\u9875\u76EE\u5F55\u7684\u5462\uFF1F\u9875\u76EE\u5F55\u4E0E\u8BB0\u5F55\u7684\u5173\u7CFB\u5982\u4E0B\u56FE\uFF1A</p><p><img src="'+e+'" alt="\u56FE\u7247"></p><p>\u9875\u76EE\u5F55\u521B\u5EFA\u7684\u8FC7\u7A0B\u5982\u4E0B\uFF1A</p><ol><li>\u5C06\u6240\u6709\u7684\u8BB0\u5F55\u5212\u5206\u6210\u51E0\u4E2A\u7EC4\uFF0C\u8FD9\u4E9B\u8BB0\u5F55\u5305\u62EC\u6700\u5C0F\u8BB0\u5F55\u548C\u6700\u5927\u8BB0\u5F55\uFF0C\u4F46\u4E0D\u5305\u62EC\u6807\u8BB0\u4E3A\u201C\u5DF2\u5220\u9664\u201D\u7684\u8BB0\u5F55\uFF1B</li><li>\u6BCF\u4E2A\u8BB0\u5F55\u7EC4\u7684\u6700\u540E\u4E00\u6761\u8BB0\u5F55\u5C31\u662F\u7EC4\u5185\u6700\u5927\u7684\u90A3\u6761\u8BB0\u5F55\uFF0C\u5E76\u4E14\u6700\u540E\u4E00\u6761\u8BB0\u5F55\u7684\u5934\u4FE1\u606F\u4E2D\u4F1A\u5B58\u50A8\u8BE5\u7EC4\u4E00\u5171\u6709\u591A\u5C11\u6761\u8BB0\u5F55\uFF0C\u4F5C\u4E3A n_owned \u5B57\u6BB5\uFF08\u4E0A\u56FE\u4E2D\u7C89\u7EA2\u8272\u5B57\u6BB5\uFF09</li><li>\u9875\u76EE\u5F55\u7528\u6765\u5B58\u50A8\u6BCF\u7EC4\u6700\u540E\u4E00\u6761\u8BB0\u5F55\u7684\u5730\u5740\u504F\u79FB\u91CF\uFF0C\u8FD9\u4E9B\u5730\u5740\u504F\u79FB\u91CF\u4F1A\u6309\u7167\u5148\u540E\u987A\u5E8F\u5B58\u50A8\u8D77\u6765\uFF0C\u6BCF\u7EC4\u7684\u5730\u5740\u504F\u79FB\u91CF\u4E5F\u88AB\u79F0\u4E4B\u4E3A\u69FD\uFF08slot\uFF09\uFF0C<strong>\u6BCF\u4E2A\u69FD\u76F8\u5F53\u4E8E\u6307\u9488\u6307\u5411\u4E86\u4E0D\u540C\u7EC4\u7684\u6700\u540E\u4E00\u4E2A\u8BB0\u5F55</strong>\u3002</li></ol><p>\u4ECE\u56FE\u53EF\u4EE5\u770B\u5230\uFF0C<strong>\u9875\u76EE\u5F55\u5C31\u662F\u7531\u591A\u4E2A\u69FD\u7EC4\u6210\u7684\uFF0C\u69FD\u76F8\u5F53\u4E8E\u5206\u7EC4\u8BB0\u5F55\u7684\u7D22\u5F15</strong>\u3002\u7136\u540E\uFF0C\u56E0\u4E3A\u8BB0\u5F55\u662F\u6309\u7167\u300C\u4E3B\u952E\u503C\u300D\u4ECE\u5C0F\u5230\u5927\u6392\u5E8F\u7684\uFF0C\u6240\u4EE5<strong>\u6211\u4EEC\u901A\u8FC7\u69FD\u67E5\u627E\u8BB0\u5F55\u65F6\uFF0C\u53EF\u4EE5\u4F7F\u7528\u4E8C\u5206\u6CD5\u5FEB\u901F\u5B9A\u4F4D\u8981\u67E5\u8BE2\u7684\u8BB0\u5F55\u5728\u54EA\u4E2A\u69FD\uFF08\u54EA\u4E2A\u8BB0\u5F55\u5206\u7EC4\uFF09\uFF0C\u5B9A\u4F4D\u5230\u69FD\u540E\uFF0C\u518D\u904D\u5386\u69FD\u5185\u7684\u6240\u6709\u8BB0\u5F55\uFF0C\u627E\u5230\u5BF9\u5E94\u7684\u8BB0\u5F55</strong>\uFF0C\u65E0\u9700\u4ECE\u6700\u5C0F\u8BB0\u5F55\u5F00\u59CB\u904D\u5386\u6574\u4E2A\u9875\u4E2D\u7684\u8BB0\u5F55\u94FE\u8868\u3002</p><p>\u4EE5\u4E0A\u9762\u90A3\u5F20\u56FE\u4E3E\u4E2A\u4F8B\u5B50\uFF0C5 \u4E2A\u69FD\u7684\u7F16\u53F7\u5206\u522B\u4E3A 0\uFF0C1\uFF0C2\uFF0C3\uFF0C4\uFF0C\u6211\u60F3\u67E5\u627E\u4E3B\u952E\u4E3A 11 \u7684\u7528\u6237\u8BB0\u5F55\uFF1A</p><ul><li>\u5148\u4E8C\u5206\u5F97\u51FA\u69FD\u4E2D\u95F4\u4F4D\u662F (0+4)/2=2 \uFF0C2\u53F7\u69FD\u91CC\u6700\u5927\u7684\u8BB0\u5F55\u4E3A 8\u3002\u56E0\u4E3A 11 &gt; 8\uFF0C\u6240\u4EE5\u9700\u8981\u4ECE 2 \u53F7\u69FD\u540E\u7EE7\u7EED\u641C\u7D22\u8BB0\u5F55\uFF1B</li><li>\u518D\u4F7F\u7528\u4E8C\u5206\u641C\u7D22\u51FA 2 \u53F7\u548C 4 \u69FD\u7684\u4E2D\u95F4\u4F4D\u662F (2+4)/2= 3\uFF0C3 \u53F7\u69FD\u91CC\u6700\u5927\u7684\u8BB0\u5F55\u4E3A 12\u3002\u56E0\u4E3A 11 &lt; 12\uFF0C\u6240\u4EE5\u4E3B\u952E\u4E3A 11 \u7684\u8BB0\u5F55\u5728 3 \u53F7\u69FD\u91CC\uFF1B</li><li>\u8FD9\u91CC\u6709\u4E2A\u95EE\u9898\uFF0C<strong>\u300C\u69FD\u5BF9\u5E94\u7684\u503C\u90FD\u662F\u8FD9\u4E2A\u7EC4\u7684\u4E3B\u952E\u6700\u5927\u7684\u8BB0\u5F55\uFF0C\u5982\u4F55\u627E\u5230\u7EC4\u91CC\u6700\u5C0F\u7684\u8BB0\u5F55\u300D</strong>\uFF1F\u6BD4\u5982\u69FD 3 \u5BF9\u5E94\u6700\u5927\u4E3B\u952E\u662F 12 \u7684\u8BB0\u5F55\uFF0C\u90A3\u5982\u4F55\u627E\u5230\u6700\u5C0F\u8BB0\u5F55 9\u3002\u89E3\u51B3\u529E\u6CD5\u662F\uFF1A\u901A\u8FC7\u69FD 3 \u627E\u5230 \u69FD 2 \u5BF9\u5E94\u7684\u8BB0\u5F55\uFF0C\u4E5F\u5C31\u662F\u4E3B\u952E\u4E3A 8 \u7684\u8BB0\u5F55\u3002\u4E3B\u952E\u4E3A 8 \u7684\u8BB0\u5F55\u7684\u4E0B\u4E00\u6761\u8BB0\u5F55\u5C31\u662F\u69FD 3 \u5F53\u4E2D\u4E3B\u952E\u6700\u5C0F\u7684 9 \u8BB0\u5F55\uFF0C\u7136\u540E\u5F00\u59CB\u5411\u4E0B\u641C\u7D22 2 \u6B21\uFF0C\u5B9A\u4F4D\u5230\u4E3B\u952E\u4E3A 11 \u7684\u8BB0\u5F55\uFF0C\u53D6\u51FA\u8BE5\u6761\u8BB0\u5F55\u7684\u4FE1\u606F\u5373\u4E3A\u6211\u4EEC\u60F3\u8981\u67E5\u627E\u7684\u5185\u5BB9\u3002</li></ul><p>\u770B\u5230\u7B2C\u4E09\u6B65\u7684\u65F6\u5019\uFF0C\u53EF\u80FD\u6709\u7684\u540C\u5B66\u4F1A\u7591\u95EE\uFF0C\u5982\u679C\u67D0\u4E2A\u69FD\u5185\u7684\u8BB0\u5F55\u5F88\u591A\uFF0C\u7136\u540E\u56E0\u4E3A\u8BB0\u5F55\u90FD\u662F\u5355\u5411\u94FE\u8868\u4E32\u8D77\u6765\u7684\uFF0C\u90A3\u8FD9\u6837\u5728\u69FD\u5185\u67E5\u627E\u67D0\u4E2A\u8BB0\u5F55\u7684\u65F6\u95F4\u590D\u6742\u5EA6\u4E0D\u5C31\u662F O(n) \u4E86\u5417\uFF1F</p><p>\u8FD9\u70B9\u4E0D\u7528\u62C5\u5FC3\uFF0CInnoDB \u5BF9\u6BCF\u4E2A\u5206\u7EC4\u4E2D\u7684\u8BB0\u5F55\u6761\u6570\u90FD\u662F\u6709\u89C4\u5B9A\u7684\uFF0C\u69FD\u5185\u7684\u8BB0\u5F55\u5C31\u53EA\u6709\u51E0\u6761\uFF1A</p><ul><li>\u7B2C\u4E00\u4E2A\u5206\u7EC4\u4E2D\u7684\u8BB0\u5F55\u53EA\u80FD\u6709 1 \u6761\u8BB0\u5F55\uFF1B</li><li>\u6700\u540E\u4E00\u4E2A\u5206\u7EC4\u4E2D\u7684\u8BB0\u5F55\u6761\u6570\u8303\u56F4\u53EA\u80FD\u5728 1-8 \u6761\u4E4B\u95F4\uFF1B</li><li>\u5269\u4E0B\u7684\u5206\u7EC4\u4E2D\u8BB0\u5F55\u6761\u6570\u8303\u56F4\u53EA\u80FD\u5728 4-8 \u6761\u4E4B\u95F4\u3002</li></ul><h2 id="b-\u6811\u662F\u5982\u4F55\u8FDB\u884C\u67E5\u8BE2\u7684" tabindex="-1"><a class="header-anchor" href="#b-\u6811\u662F\u5982\u4F55\u8FDB\u884C\u67E5\u8BE2\u7684" aria-hidden="true">#</a> B+ \u6811\u662F\u5982\u4F55\u8FDB\u884C\u67E5\u8BE2\u7684\uFF1F</h2><p>\u4E0A\u9762\u6211\u4EEC\u90FD\u662F\u5728\u8BF4\u4E00\u4E2A\u6570\u636E\u9875\u4E2D\u7684\u8BB0\u5F55\u68C0\u7D22\uFF0C\u56E0\u4E3A\u4E00\u4E2A\u6570\u636E\u9875\u4E2D\u7684\u8BB0\u5F55\u662F\u6709\u9650\u7684\uFF0C\u4E14\u4E3B\u952E\u503C\u662F\u6709\u5E8F\u7684\uFF0C\u6240\u4EE5\u901A\u8FC7\u5BF9\u6240\u6709\u8BB0\u5F55\u8FDB\u884C\u5206\u7EC4\uFF0C\u7136\u540E\u5C06\u7EC4\u53F7\uFF08\u69FD\u53F7\uFF09\u5B58\u50A8\u5230\u9875\u76EE\u5F55\uFF0C\u4F7F\u5176\u8D77\u5230\u7D22\u5F15\u4F5C\u7528\uFF0C\u901A\u8FC7\u4E8C\u5206\u67E5\u627E\u7684\u65B9\u6CD5\u5FEB\u901F\u68C0\u7D22\u5230\u8BB0\u5F55\u5728\u54EA\u4E2A\u5206\u7EC4\uFF0C\u6765\u964D\u4F4E\u68C0\u7D22\u7684\u65F6\u95F4\u590D\u6742\u5EA6\u3002</p><p>\u4F46\u662F\uFF0C\u5F53\u6211\u4EEC\u9700\u8981\u5B58\u50A8\u5927\u91CF\u7684\u8BB0\u5F55\u65F6\uFF0C\u5C31\u9700\u8981\u591A\u4E2A\u6570\u636E\u9875\uFF0C\u8FD9\u65F6\u6211\u4EEC\u5C31\u9700\u8981\u8003\u8651\u5982\u4F55\u5EFA\u7ACB\u5408\u9002\u7684\u7D22\u5F15\uFF0C\u624D\u80FD\u65B9\u4FBF\u5B9A\u4F4D\u8BB0\u5F55\u6240\u5728\u7684\u9875\u3002</p><p>\u4E3A\u4E86\u89E3\u51B3\u8FD9\u4E2A\u95EE\u9898\uFF0C<strong>InnoDB \u91C7\u7528\u4E86 B+ \u6811\u4F5C\u4E3A\u7D22\u5F15</strong>\u3002\u78C1\u76D8\u7684 I/O \u64CD\u4F5C\u6B21\u6570\u5BF9\u7D22\u5F15\u7684\u4F7F\u7528\u6548\u7387\u81F3\u5173\u91CD\u8981\uFF0C\u56E0\u6B64\u5728\u6784\u9020\u7D22\u5F15\u7684\u65F6\u5019\uFF0C\u6211\u4EEC\u66F4\u503E\u5411\u4E8E\u91C7\u7528\u201C\u77EE\u80D6\u201D\u7684 B+ \u6811\u6570\u636E\u7ED3\u6784\uFF0C\u8FD9\u6837\u6240\u9700\u8981\u8FDB\u884C\u7684\u78C1\u76D8 I/O \u6B21\u6570\u66F4\u5C11\uFF0C\u800C\u4E14 B+ \u6811 \u66F4\u9002\u5408\u8FDB\u884C\u5173\u952E\u5B57\u7684\u8303\u56F4\u67E5\u8BE2\u3002</p><p>InnoDB \u91CC\u7684 B+ \u6811\u4E2D\u7684<strong>\u6BCF\u4E2A\u8282\u70B9\u90FD\u662F\u4E00\u4E2A\u6570\u636E\u9875</strong>\uFF0C\u7ED3\u6784\u793A\u610F\u56FE\u5982\u4E0B\uFF1A</p><p><img src="'+r+'" alt="\u56FE\u7247"></p><p>\u901A\u8FC7\u4E0A\u56FE\uFF0C\u6211\u4EEC\u770B\u51FA B+ \u6811\u7684\u7279\u70B9\uFF1A</p><details class="custom-container details"><ul><li>\u53EA\u6709\u53F6\u5B50\u8282\u70B9\uFF08\u6700\u5E95\u5C42\u7684\u8282\u70B9\uFF09\u624D\u5B58\u653E\u4E86\u6570\u636E\uFF0C\u975E\u53F6\u5B50\u8282\u70B9\uFF08\u5176\u4ED6\u4E0A\u5C42\u8282\uFF09\u4EC5\u7528\u6765\u5B58\u653E\u76EE\u5F55\u9879\u4F5C\u4E3A\u7D22\u5F15\u3002</li><li>\u975E\u53F6\u5B50\u8282\u70B9\u5206\u4E3A\u4E0D\u540C\u5C42\u6B21\uFF0C\u901A\u8FC7\u5206\u5C42\u6765\u964D\u4F4E\u6BCF\u4E00\u5C42\u7684\u641C\u7D22\u91CF\uFF1B</li><li>\u6240\u6709\u8282\u70B9\u6309\u7167\u7D22\u5F15\u952E\u5927\u5C0F\u6392\u5E8F\uFF0C\u6784\u6210\u4E00\u4E2A\u53CC\u5411\u94FE\u8868\uFF0C\u4FBF\u4E8E\u8303\u56F4\u67E5\u8BE2\uFF1B</li></ul></details><p>\u6211\u4EEC\u518D\u770B\u770B B+ \u6811\u5982\u4F55\u5B9E\u73B0\u5FEB\u901F\u67E5\u627E\u4E3B\u952E\u4E3A 6 \u7684\u8BB0\u5F55\uFF0C\u4EE5\u4E0A\u56FE\u4E3A\u4F8B\u5B50\uFF1A</p><details class="custom-container details"><ul><li>\u4ECE\u6839\u8282\u70B9\u5F00\u59CB\uFF0C\u901A\u8FC7\u4E8C\u5206\u6CD5\u5FEB\u901F\u5B9A\u4F4D\u5230\u7B26\u5408\u9875\u5185\u8303\u56F4\u5305\u542B\u67E5\u8BE2\u503C\u7684\u9875\uFF0C\u56E0\u4E3A\u67E5\u8BE2\u7684\u4E3B\u952E\u503C\u4E3A 6\uFF0C\u5728[1, 7)\u8303\u56F4\u4E4B\u95F4\uFF0C\u6240\u4EE5\u5230\u9875 30 \u4E2D\u67E5\u627E\u66F4\u8BE6\u7EC6\u7684\u76EE\u5F55\u9879\uFF1B</li><li>\u5728\u975E\u53F6\u5B50\u8282\u70B9\uFF08\u987530\uFF09\u4E2D\uFF0C\u7EE7\u7EED\u901A\u8FC7\u4E8C\u5206\u6CD5\u5FEB\u901F\u5B9A\u4F4D\u5230\u7B26\u5408\u9875\u5185\u8303\u56F4\u5305\u542B\u67E5\u8BE2\u503C\u7684\u9875\uFF0C\u4E3B\u952E\u503C\u5927\u4E8E 5\uFF0C\u6240\u4EE5\u5C31\u5230\u53F6\u5B50\u8282\u70B9\uFF08\u987516\uFF09\u67E5\u627E\u8BB0\u5F55\uFF1B</li><li>\u63A5\u7740\uFF0C\u5728\u53F6\u5B50\u8282\u70B9\uFF08\u987516\uFF09\u4E2D\uFF0C\u901A\u8FC7\u69FD\u67E5\u627E\u8BB0\u5F55\u65F6\uFF0C\u4F7F\u7528\u4E8C\u5206\u6CD5\u5FEB\u901F\u5B9A\u4F4D\u8981\u67E5\u8BE2\u7684\u8BB0\u5F55\u5728\u54EA\u4E2A\u69FD\uFF08\u54EA\u4E2A\u8BB0\u5F55\u5206\u7EC4\uFF09\uFF0C\u5B9A\u4F4D\u5230\u69FD\u540E\uFF0C\u518D\u904D\u5386\u69FD\u5185\u7684\u6240\u6709\u8BB0\u5F55\uFF0C\u627E\u5230\u4E3B\u952E\u4E3A 6 \u7684\u8BB0\u5F55\u3002</li></ul></details><p>\u53EF\u4EE5\u770B\u5230\uFF0C\u5728\u5B9A\u4F4D\u8BB0\u5F55\u6240\u5728\u54EA\u4E00\u4E2A\u9875\u65F6\uFF0C\u4E5F\u662F\u901A\u8FC7\u4E8C\u5206\u6CD5\u5FEB\u901F\u5B9A\u4F4D\u5230\u5305\u542B\u8BE5\u8BB0\u5F55\u7684\u9875\u3002\u5B9A\u4F4D\u5230\u8BE5\u9875\u540E\uFF0C\u53C8\u4F1A\u5728\u8BE5\u9875\u5185\u8FDB\u884C\u4E8C\u5206\u6CD5\u5FEB\u901F\u5B9A\u4F4D\u8BB0\u5F55\u6240\u5728\u7684\u5206\u7EC4\uFF08\u69FD\u53F7\uFF09\uFF0C\u6700\u540E\u5728\u5206\u7EC4\u5185\u8FDB\u884C\u904D\u5386\u67E5\u627E\u3002</p><h2 id="\u805A\u7C07\u7D22\u5F15\u548C\u4E8C\u7EA7\u7D22\u5F15" tabindex="-1"><a class="header-anchor" href="#\u805A\u7C07\u7D22\u5F15\u548C\u4E8C\u7EA7\u7D22\u5F15" aria-hidden="true">#</a> \u805A\u7C07\u7D22\u5F15\u548C\u4E8C\u7EA7\u7D22\u5F15</h2><p>\u53E6\u5916\uFF0C\u7D22\u5F15\u53C8\u53EF\u4EE5\u5206\u6210\u805A\u7C07\u7D22\u5F15\u548C\u975E\u805A\u7C07\u7D22\u5F15\uFF08\u4E8C\u7EA7\u7D22\u5F15\uFF09\uFF0C\u5B83\u4EEC\u533A\u522B\u5C31\u5728\u4E8E\u53F6\u5B50\u8282\u70B9\u5B58\u653E\u7684\u662F\u4EC0\u4E48\u6570\u636E\uFF1A</p><ul><li>\u805A\u7C07\u7D22\u5F15\u7684\u53F6\u5B50\u8282\u70B9\u5B58\u653E\u7684\u662F\u5B9E\u9645\u6570\u636E\uFF0C\u6240\u6709\u5B8C\u6574\u7684\u7528\u6237\u8BB0\u5F55\u90FD\u5B58\u653E\u5728\u805A\u7C07\u7D22\u5F15\u7684\u53F6\u5B50\u8282\u70B9\uFF1B</li><li>\u4E8C\u7EA7\u7D22\u5F15\u7684\u53F6\u5B50\u8282\u70B9\u5B58\u653E\u7684\u662F\u4E3B\u952E\u503C\uFF0C\u800C\u4E0D\u662F\u5B9E\u9645\u6570\u636E\u3002</li></ul><p>\u56E0\u4E3A\u8868\u7684\u6570\u636E\u90FD\u662F\u5B58\u653E\u5728\u805A\u7C07\u7D22\u5F15\u7684\u53F6\u5B50\u8282\u70B9\u91CC\uFF0C\u6240\u4EE5 InnoDB \u5B58\u50A8\u5F15\u64CE\u4E00\u5B9A\u4F1A\u4E3A\u8868\u521B\u5EFA\u4E00\u4E2A\u805A\u7C07\u7D22\u5F15\uFF0C\u4E14\u7531\u4E8E\u6570\u636E\u5728\u7269\u7406\u4E0A\u53EA\u4F1A\u4FDD\u5B58\u4E00\u4EFD\uFF0C\u6240\u4EE5\u805A\u7C07\u7D22\u5F15\u53EA\u80FD\u6709\u4E00\u4E2A\u3002</p><p>InnoDB \u5728\u521B\u5EFA\u805A\u7C07\u7D22\u5F15\u65F6\uFF0C\u4F1A\u6839\u636E\u4E0D\u540C\u7684\u573A\u666F\u9009\u62E9\u4E0D\u540C\u7684\u5217\u4F5C\u4E3A\u7D22\u5F15\uFF1A</p><details class="custom-container details"><ul><li>\u5982\u679C\u6709\u4E3B\u952E\uFF0C\u9ED8\u8BA4\u4F1A\u4F7F\u7528\u4E3B\u952E\u4F5C\u4E3A\u805A\u7C07\u7D22\u5F15\u7684\u7D22\u5F15\u952E\uFF1B</li><li>\u5982\u679C\u6CA1\u6709\u4E3B\u952E\uFF0C\u5C31\u9009\u62E9\u7B2C\u4E00\u4E2A\u4E0D\u5305\u542B NULL \u503C\u7684\u552F\u4E00\u5217\u4F5C\u4E3A\u805A\u7C07\u7D22\u5F15\u7684\u7D22\u5F15\u952E\uFF1B</li><li>\u5728\u4E0A\u9762\u4E24\u4E2A\u90FD\u6CA1\u6709\u7684\u60C5\u51B5\u4E0B\uFF0CInnoDB \u5C06\u81EA\u52A8\u751F\u6210\u4E00\u4E2A\u9690\u5F0F\u81EA\u589E id \u5217\u4F5C\u4E3A\u805A\u7C07\u7D22\u5F15\u7684\u7D22\u5F15\u952E\uFF1B</li></ul></details><p>\u4E00\u5F20\u8868\u53EA\u80FD\u6709\u4E00\u4E2A\u805A\u7C07\u7D22\u5F15\uFF0C\u90A3\u4E3A\u4E86\u5B9E\u73B0\u975E\u4E3B\u952E\u5B57\u6BB5\u7684\u5FEB\u901F\u641C\u7D22\uFF0C\u5C31\u5F15\u51FA\u4E86\u4E8C\u7EA7\u7D22\u5F15\uFF08\u975E\u805A\u7C07\u7D22\u5F15/\u8F85\u52A9\u7D22\u5F15\uFF09\uFF0C\u5B83\u4E5F\u662F\u5229\u7528\u4E86 B+ \u6811\u7684\u6570\u636E\u7ED3\u6784\uFF0C\u4F46\u662F\u4E8C\u7EA7\u7D22\u5F15\u7684\u53F6\u5B50\u8282\u70B9\u5B58\u653E\u7684\u662F\u4E3B\u952E\u503C\uFF0C\u4E0D\u662F\u5B9E\u9645\u6570\u636E\u3002</p><p>\u4E8C\u7EA7\u7D22\u5F15\u7684 B+ \u6811\u5982\u4E0B\u56FE\uFF0C\u6570\u636E\u90E8\u5206\u4E3A\u4E3B\u952E\u503C\uFF1A</p><p><img src="'+l+'" alt="\u56FE\u7247"></p><p>\u56E0\u6B64\uFF0C<strong>\u5982\u679C\u67D0\u4E2A\u67E5\u8BE2\u8BED\u53E5\u4F7F\u7528\u4E86\u4E8C\u7EA7\u7D22\u5F15\uFF0C\u4F46\u662F\u67E5\u8BE2\u7684\u6570\u636E\u4E0D\u662F\u4E3B\u952E\u503C\uFF0C\u8FD9\u65F6\u5728\u4E8C\u7EA7\u7D22\u5F15\u627E\u5230\u4E3B\u952E\u503C\u540E\uFF0C\u9700\u8981\u53BB\u805A\u7C07\u7D22\u5F15\u4E2D\u83B7\u5F97\u6570\u636E\u884C\uFF0C\u8FD9\u4E2A\u8FC7\u7A0B\u5C31\u53EB\u4F5C\u300C\u56DE\u8868\u300D\uFF0C\u4E5F\u5C31\u662F\u8BF4\u8981\u67E5\u4E24\u4E2A B+ \u6811\u624D\u80FD\u67E5\u5230\u6570\u636E\u3002\u4E0D\u8FC7\uFF0C\u5F53\u67E5\u8BE2\u7684\u6570\u636E\u662F\u4E3B\u952E\u503C\u65F6\uFF0C\u56E0\u4E3A\u53EA\u5728\u4E8C\u7EA7\u7D22\u5F15\u5C31\u80FD\u67E5\u8BE2\u5230\uFF0C\u4E0D\u7528\u518D\u53BB\u805A\u7C07\u7D22\u5F15\u67E5\uFF0C\u8FD9\u4E2A\u8FC7\u7A0B\u5C31\u53EB\u4F5C\u300C\u7D22\u5F15\u8986\u76D6\u300D\uFF0C\u4E5F\u5C31\u662F\u53EA\u9700\u8981\u67E5\u4E00\u4E2A B+ \u6811\u5C31\u80FD\u627E\u5230\u6570\u636E\u3002</strong></p><h2 id="\u603B\u7ED3" tabindex="-1"><a class="header-anchor" href="#\u603B\u7ED3" aria-hidden="true">#</a> \u603B\u7ED3</h2><p>InnoDB \u7684\u6570\u636E\u662F\u6309\u300C\u6570\u636E\u9875\u300D\u4E3A\u5355\u4F4D\u6765\u8BFB\u5199\u7684\uFF0C\u9ED8\u8BA4\u6570\u636E\u9875\u5927\u5C0F\u4E3A 16 KB\u3002\u6BCF\u4E2A\u6570\u636E\u9875\u4E4B\u95F4\u901A\u8FC7\u53CC\u5411\u94FE\u8868\u7684\u5F62\u5F0F\u7EC4\u7EC7\u8D77\u6765\uFF0C\u7269\u7406\u4E0A\u4E0D\u8FDE\u7EED\uFF0C\u4F46\u662F\u903B\u8F91\u4E0A\u8FDE\u7EED\u3002</p><p>\u6570\u636E\u9875\u5185\u5305\u542B\u7528\u6237\u8BB0\u5F55\uFF0C\u6BCF\u4E2A\u8BB0\u5F55\u4E4B\u95F4\u7528\u5355\u5411\u94FE\u8868\u7684\u65B9\u5F0F\u7EC4\u7EC7\u8D77\u6765\uFF0C\u4E3A\u4E86\u52A0\u5FEB\u5728\u6570\u636E\u9875\u5185\u9AD8\u6548\u67E5\u8BE2\u8BB0\u5F55\uFF0C\u8BBE\u8BA1\u4E86\u4E00\u4E2A\u9875\u76EE\u5F55\uFF0C\u9875\u76EE\u5F55\u5B58\u50A8\u5404\u4E2A\u69FD\uFF08\u5206\u7EC4\uFF09\uFF0C\u4E14\u4E3B\u952E\u503C\u662F\u6709\u5E8F\u7684\uFF0C\u4E8E\u662F\u53EF\u4EE5\u901A\u8FC7\u4E8C\u5206\u67E5\u627E\u6CD5\u7684\u65B9\u5F0F\u8FDB\u884C\u68C0\u7D22\u4ECE\u800C\u63D0\u9AD8\u6548\u7387\u3002</p><p>\u4E3A\u4E86\u9AD8\u6548\u67E5\u8BE2\u8BB0\u5F55\u6240\u5728\u7684\u6570\u636E\u9875\uFF0CInnoDB \u91C7\u7528 b+ \u6811\u4F5C\u4E3A\u7D22\u5F15\uFF0C\u6BCF\u4E2A\u8282\u70B9\u90FD\u662F\u4E00\u4E2A\u6570\u636E\u9875\u3002</p><p>\u5982\u679C\u53F6\u5B50\u8282\u70B9\u5B58\u50A8\u7684\u662F\u5B9E\u9645\u6570\u636E\u7684\u5C31\u662F\u805A\u7C07\u7D22\u5F15\uFF0C\u4E00\u4E2A\u8868\u53EA\u80FD\u6709\u4E00\u4E2A\u805A\u7C07\u7D22\u5F15\uFF1B\u5982\u679C\u53F6\u5B50\u8282\u70B9\u5B58\u50A8\u7684\u4E0D\u662F\u5B9E\u9645\u6570\u636E\uFF0C\u800C\u662F\u4E3B\u952E\u503C\u5219\u5C31\u662F\u4E8C\u7EA7\u7D22\u5F15\uFF0C\u4E00\u4E2A\u8868\u4E2D\u53EF\u4EE5\u6709\u591A\u4E2A\u4E8C\u7EA7\u7D22\u5F15\u3002</p><p>\u5728\u4F7F\u7528\u4E8C\u7EA7\u7D22\u5F15\u8FDB\u884C\u67E5\u627E\u6570\u636E\u65F6\uFF0C\u5982\u679C\u67E5\u8BE2\u7684\u6570\u636E\u80FD\u5728\u4E8C\u7EA7\u7D22\u5F15\u627E\u5230\uFF0C\u90A3\u4E48\u5C31\u662F\u300C\u7D22\u5F15\u8986\u76D6\u300D\u64CD\u4F5C\uFF0C\u5982\u679C\u67E5\u8BE2\u7684\u6570\u636E\u4E0D\u5728\u4E8C\u7EA7\u7D22\u5F15\u91CC\uFF0C\u5C31\u9700\u8981\u5148\u5728\u4E8C\u7EA7\u7D22\u5F15\u627E\u5230\u4E3B\u952E\u503C\uFF0C\u9700\u8981\u53BB\u805A\u7C07\u7D22\u5F15\u4E2D\u83B7\u5F97\u6570\u636E\u884C\uFF0C\u8FD9\u4E2A\u8FC7\u7A0B\u5C31\u53EB\u4F5C\u300C\u56DE\u8868\u300D\u3002</p><p>\u5173\u4E8E\u7D22\u5F15\u7684\u5185\u5BB9\u8FD8\u6709\u5F88\u591A\uFF0C\u6BD4\u5982\u7D22\u5F15\u5931\u6548\u3001\u7D22\u5F15\u4F18\u5316\u7B49\u7B49\uFF0C\u8FD9\u4E9B\u5185\u5BB9\u6211\u4E0B\u6B21\u5728\u8BB2\u5566\uFF01</p>',60);function c(g,h){return d}var _=a(o,[["render",c],["__file","data_btree.html.vue"]]);export{_ as default};
